INSERT INTO `g_test_content_module_bak` VALUES ('1140', null, '2', null, 'A   B   C', '0', '六个月婴儿有密切结核接触史，预防措施是\n\n', '小儿结核病', '小儿原发性肺结核,诊断和鉴别诊断', '1', '2', '2', '1', '1', '2', '0.00', 'A.隔离患者               B.立即接种卡介苗          C.口服异烟肼\n\nD.肌注链霉素             E.作OT试验');
