INSERT INTO `g_test_content_module_bak` VALUES ('1221', null, '7', null, '1.脑脊液压力增高\n\n2.外观混浊甚至脓样\n\n3.白细胞多在1000×106/L以上，以中性粒细胞为主\n\n4.糖含量降低，', '0', '试述细菌性脑膜炎的脑脊液典型改变', '神经系统疾病', '细菌性脑膜炎,临床表现和辅助诊断', '1', '1', '2', '1', '3', '2', '0.00', '');
INSERT INTO `g_test_content_module_bak` VALUES ('1222', null, '7', null, '新生儿和幼婴由于颅骨骨缝未闭而易于分离，因而颅内高压的高压症状可不\n\n明显，又因机体反应性差，表现为体温不升，神萎不良，面色灰白。并发败血症\n\n时，常有黄疸存在。', '0', '新生儿和幼婴细菌性脑膜炎的临床表现及其特点', '神经系统疾病', '细菌性脑膜炎,临床表现和辅助诊断', '1', '1', '2', '3', '1', '1', '0.04', '');
