INSERT INTO `g_test_content_module_bak` VALUES ('1313', null, '2', null, 'A   C', '0', '急性尿路感染的抗生素疗程一般包括下列几项：\n\n', '泌尿系统疾病', '尿路感染,治疗', '2', '2', '2', '1', '1', '1', '0.00', 'A.急性感染一般用药10-14天           B.用到尿中细菌转阴\n\nC.肾盂肾炎可适当延长至6-8周         D.尿常规正常3-5天\n\nE.常常用药1-2个月');
