INSERT INTO `g_test_content_module_bak` VALUES ('1462', null, '2', null, 'A   D', '0', '小于胎龄儿可以与下列疾病有关\n\n', '新生儿疾病', '新生儿,新生儿特点', '2', '1', '2', '1', '1', '2', '0.00', 'A.Turner综合征                 B.母亲患糖尿病\n\nC.胎儿患有Rh血型不合           D.巨细胞病毒感染\n\nE.大血管错位先天性心脏病');
INSERT INTO `g_test_content_module_bak` VALUES ('1463', null, '2', null, 'A   B', '0', '新生儿特点正确的有\n\n', '新生儿疾病', '新生儿,新生儿特点', '2', '1', '2', '1', '1', '2', '0.00', 'A.初生婴儿液体需要量与体重有关\n\nB.初生婴儿液体需要量与日龄有关\n\nC.新生儿生后3天内血钾水平较高，不需补充\n\nD.乳腺肿大多于1周左右消退，不需处理\n\nE.部分女婴生后1-2天可见阴道出血，持续5-7天自止');
INSERT INTO `g_test_content_module_bak` VALUES ('1464', null, '2', null, 'A   B   C', '0', '新生儿特点不正确的有\n\n', '新生儿疾病', '新生儿,新生儿特点', '2', '1', '2', '1', '1', '2', '0.00', 'A.与牙本板于1-2周消退不宜挑刮    B.早产儿要按日龄选择适中温度\n\nC.早产儿配方乳，开始按3:1稀释    D.早产儿补充维生素');
INSERT INTO `g_test_content_module_bak` VALUES ('1465', null, '2', null, 'B   C   D', '0', '关于早产儿特点，下列哪项是错误的？\n\n', '新生儿疾病', '新生儿,新生儿特点', '2', '1', '2', '1', '1', '2', '0.00', 'A.早产儿易发生低血糖、低血钙、低血钠症\n\nB.早产儿水的需要量相对较足月儿低\n\nC.常有呼吸停止20秒钟以上，心率减慢');
INSERT INTO `g_test_content_module_bak` VALUES ('1466', null, '2', null, 'D   E', '0', '宫内生长迟缓综合征的分型正确的有\n\n', '新生儿疾病', '新生儿,新生儿特点', '2', '1', '2', '1', '1', '2', '0.00', 'A.根据胎儿体型匀称程度分为三型\n\nB.非匀称型常与遗传，代谢缺陷及宫内感染有关\n\nC.匀称型各器官细胞数量正常，但细胞变小\n\nD.非匀称型损伤为可逆性\n\nE.匀称型在妊娠早期生长即受损');
INSERT INTO `g_test_content_module_bak` VALUES ('1467', null, '2', null, 'A B C', '0', '小于胎龄儿常见并发症有\n', '新生儿疾病', '新生儿,新生儿特点', '2', '1', '2', '1', '1', '2', '0.00', 'A.围产期窒息      B.胎类吸入       C.红细胞增多-高粉滞血综合征\nD.反复呼吸暂停    E.肺透明膜病');
