INSERT INTO `g_test_content_module_bak` VALUES ('1488', null, '1', null, 'D', '0', '早产儿，体温33℃，反应低下，不能吸吮，四肢，驱干，臀部均呈硬肿。忽然出现面色发绀，呼吸困难，肺部可闻及细湿罗音。初步考虑并发\n\n', '新生儿疾病', '新生儿硬肿症,临床表现及诊断', '3', '1', '2', '1', '1', '2', '0.00', 'A.急性心力衰竭        B.脓气胸                C.重症肺炎\n\nD.肺出血              E.急性呼吸衰竭');
