INSERT INTO `g_test_content_module_bak` VALUES ('1856', null, '1', null, 'B', '0', '患儿G2P1，生后母乳喂养，42天时因发热去医院就诊，体检发现头面部及胸腹部皮肤黄染，肝肋下3cm,质中。以下哪一项与以上临床表现无关？ \n', '新生儿疾病', '新生儿黄症,生理性黄症', '2', '1', '2', '3', '2', '3', '0.18', 'A.新生儿母乳性黄疸 \nB.新生儿生理性贫血 \nC.婴儿肝炎综合征 \nD.新生儿尿感 \nE.新生儿败血症');
